INSERT INTO `evaluation_criterias` (`id`, `criteria_title`, `type`, `desciption`, `weighting`, `status`, `createdBy`, `updatedBy`, `createdAt`, `updatedAt`, `deleted`) VALUES
(1, 'Customer Centricity', 2, 'Has understood the customer problem and the actively looks to solve it.\r\nThe idea empathizes with the customer and tries to imagine how their journey would change after the idea was implemented. ', 30, 1, NULL, NULL, '2020-03-12 07:38:10', '2023-01-19 06:52:47', 0),
(2, 'Disruptive', 2, 'The idea could be a true game changer for the enterprise.\r\nIt may have some operational barriers, but the idea harnesses the knowledge base within the enterprise and gives us a competitive edge in the long-term.\r\n', 30, 1, NULL, NULL, '2020-03-12 07:39:26', '2020-03-26 07:54:55', 0),
(3, 'Simplicity', 2, 'The idea is well thought through and clearly articulated.\r\n', 20, 1, NULL, NULL, '2020-03-24 06:09:29', '2020-03-26 07:55:33', 0),
(4, 'Scaleable', 2, 'The idea considers the enterprise\'s future product roadmap and cross-product integration.', 20, 1, NULL, NULL, '2020-03-24 06:09:56', '2020-03-26 07:57:45', 0),
(5, 'Innovative', 1, 'We want the movement to harness the looniest of ideas in hope of driving serendipitous new discoveries.\r\nWhat evidence has been provided proving the idea is original, has foresight?', 35, 1, NULL, NULL, '2023-01-18 13:02:57', '2023-01-20 05:24:23', 0),
(6, 'Vision for Impact', 1, 'To succeed, teams need to be able to formulate and communicate a clear and compelling vision for impact/technology to their stakeholders.\r\nDoes the innovation team have an exciting future vision for impact/technology that you want to invest in?\r\n', 20, 1, NULL, NULL, '2023-01-18 13:02:57', '2023-01-20 05:25:24', 0),
(7, 'Feasibility', 1, 'We want the idea owners to not only demonstrate imagination, but come determine a scientific method for proving their hypothesis.\r\nDo you believe that this innovation could realistically be developed, implemented and sustained?', 30, 1, NULL, NULL, '2023-01-18 13:02:57', '2023-01-20 05:25:16', 0),
(8, 'Viability', 1, 'We don’t necessarily want applications for the innovation, but theories on how they could scale-up over time.\r\nDo you believe that the potential innovation is sustainable over time?', 15, 1, NULL, NULL, '2023-01-18 13:02:57', '2023-01-20 05:24:03', 0),
(9, 'ashish criteria_title new', 1, 'desciption', 30, 1, NULL, NULL, '2024-03-04 12:18:54', '2024-03-04 12:35:08', 1);
